    public static RpRegistrarCertificate GetRpRegistrarCertificateSample()
    {
        var base64 = RpRegistrarPubKeyStrSample
            .Replace("-----BEGIN CERTIFICATE-----", "")
            .Replace("-----END CERTIFICATE-----", "")
            .Replace("\n", "")
            .Replace("\r", "");

        var certBytes = Convert.FromBase64String(base64);
        var parser = new X509CertificateParser();
        var cert = parser.ReadCertificate(certBytes);

        return new RpRegistrarCertificate(cert);
    }
}
